UPDATE help SET data = E'[{"id":"box1help1","title":"La menteuse","status":"open","hints":[{"id":1,"text":["Tout d\'abord, regardons si on ne pourrait pas relier un paragraphe du poème à un dossier en particulier.","Celui-là parle d\'une femme et d\'un billet de loterie."],"image":null},{"id":2,"text":["Le paragraphe de la menteuse parle de loterie.","On va donc probablement s\'intéresser à Rebecca Dumont,","le dossier de la brigade de Mulhouse."],"image":null},{"id":3,"text":["Vous avez réussi à refaire le carré sacré ?","La somme de toutes les lignes doit faire 34.","Sinon, vous pouvez chercher sur Internet, on l\'appelle aussi carré magique ou carré de Durer."],"image":null},{"id":4,"text":["Voici la résolution du carré :"],"image":"assets/renforts/box1_menteuse_renfort4_carre_magique.jpg"},{"id":5,"text":["Voici ce qu\'on obtenait en résolvant le carré.","On pourrait aller là-bas.","Vous avez interrogé le mari de Rebecca au fait ?","Son nom est sur le billet d\'avion."],"image":"assets/renforts/box1_menteuse_renfort5_adresse.jpg"},{"id":6,"text":["Vous avez fait un tour sur le lieu qui était indiqué sur le site du billet de loterie, une fois qu\'on avait fini le carré ?","Il me semble que l\'adresse c\'était","11 Rue des Trois Rois, Mulhouse."],"image":null},{"id":7,"text":["Ce voyant semble louche.","Vous avez cherché sur internet \'Nozastrum\' ou \'Nostrazum\' ?","Ou demandé à Tim de le faire ?"],"image":null},{"id":8,"text":["Le vrai nom du voyant est sur le titre de son LinkedIn :","\'Alphonse Saint Yves\'.","Si on allait l\'interroger ?"],"image":null},{"id":9,"text":["Ce cadenas directionnel...","Qu\'est-ce qui pourrait nous donner des directions ?"],"image":null},{"id":10,"text":["Je crois qu\'Alphonse le voyant nous parlait de la mort qui riait sur le canapé et de directions...","Peut-être que ça correspond aux traces de pas de la victime avant de mourir.","Vous les avez vues sur le rapport ?"],"image":null},{"id":11,"text":["La combinaison est","haut-droite-bas-droite-bas."],"image":null},{"id":12,"text":["On a trouvé un médaillon et un enregistrement dans la malle.","Vous trouverez la transcription dans l\'historique."],"image":null},{"id":13,"text":["Si on se fie à l\'enregistrement, les derniers mots de Rebecca étaient","“J\'aurais dû la sauver”"],"image":null},{"id":14,"text":["Le type de tueur…","C\'est du travail de profiler, ça.","Pensez à consulter le Guide du profiler fourni par Lauren."],"image":null}]},{"id":"box1help2","title":"L\'oublié","status":"open","hints":[{"id":1,"text":["Tout d\'abord, regardons si on ne pourrait pas relier un paragraphe du poème à un dossier en particulier.","Celui-ci parle de lettres cryptées."],"image":null},{"id":2,"text":["Il y a des lettres cryptées dans le dossier de la brigade de Châtenay-Malabry,","c\'est probablement celui-là.","Vérifiez que vous avez bien tous les documents avant de continuer."],"image":null},{"id":3,"text":["Il y a des articles de journaux dans ce dossier.","Avez-vous interrogé la journaliste qui a reçu la première lettre ?"],"image":null},{"id":4,"text":["Avez-vous envoyé un e-mail pour demander au fan plus d\'informations","(et regardé vos spams) ?"],"image":null},{"id":5,"text":["J\'ai reçu un mail automatique de réponse en envoyant le mail.","Le fan dit qu\'il ne répond plus aux mails mais qu\'il a gardé un site web sur le tueur au tarot :","https://www.fan-tueur-tarot.fr"],"image":null},{"id":6,"text":["Je trouve que la page la plus utile est celle des lettres cryptées.","La transcription la plus intéressante est la dernière sur le site.","Elle parle de ","\'L UNE SUR L AUTRE ELLES REVELERONT CELUI QUI RESTE INCONNU MAIS IL FAUT GARDER LES VIDES\'."],"image":null},{"id":7,"text":["Vous avez superposé les deux lettres pour voir s\'il restait des cases vides ?"],"image":null},{"id":8,"text":["Je lis \'Xavier Monrency\' et vous ?","On devrait consulter les archives sur cette personne grâce à Céline maintenant qu\'on sait qui c\'est."],"image":"assets/renforts/box1_oublie_renfort8_lettre_cryptee.jpg"},{"id":9,"text":["Est-ce que Xavier Monrency ne serait pas la personne \'oubliée\' dont parle le poème du tueur ?"],"image":null}]},{"id":"box1help3","title":"L\'orgueilleux","status":"open","hints":[{"id":1,"text":["Tout d\'abord, regardons si on ne pourrait pas relier un paragraphe du poème à un dossier en particulier.","Celui-ci parle de constellations."],"image":null},{"id":2,"text":["Il y a un poster avec des constellations dans le dossier de la brigade de Nantes,","dans les affaires de Charles Garraud.","Vérifiez que vous avez bien tous les éléments du dossier."],"image":null},{"id":3,"text":["Regardons bien ce poster des constellations.","On dirait que chaque case parle d\'une personne qui regardait vers le ciel au moment de sa mort.","Peut-être qu\'il faut identifier les victimes du tueur au Tarot ?"],"image":null},{"id":4,"text":["L\'une des victimes ne semble pas coller...","Peut-être qu\'on peut la trouver grâce à un autre dossier ?"],"image":null},{"id":5,"text":["Latitude et longitude... On dirait des coordonnées GPS.","Chaque personne nous donnerait-elle un chiffre ?"],"image":null},{"id":6,"text":["La carte des étoiles doit pouvoir nous aider à savoir comment était le ciel au moment où les victimes sont mortes.","J\'ai vu sur les notes de Céline qu\'elle avait interrogé un astronome à propos de cet objet.","On peut peut-être retrouver la personne.","Et il doit aussi exister des tutoriels sur le net aussi pour savoir comment s\'en servir."],"image":null},{"id":7,"text":["Je pense que la personne qui n\'a pas pu téléphoner,","c\'est Marianne Chauve.","Regardez le rapport, ça dit que le téléphone était coupé.","Toujours d\'après le rapport, elle est morte le 28/02/1992 à 2h10.","Si on positionne le calque des heures sur celui de la date, on obtient cela.","Il faut qu\'on regarde dans l\'espace vide maintenant pour savoir ce qu\'elle regardait."],"image":"assets/renforts/box1_orgueilleux_renfort6_carte_ciel.jpg"},{"id":8,"text":["Nicolas Chaussée ne semble pas correspondre à la description de la victime...","Est-ce qu\'il y en aurait une autre quelque part qui aurait été oubliée ?"],"image":null},{"id":9,"text":["Si on regarde la consigne, cela dit pour la latitude :","\'La plus longue au Sud\'.","Ce serait donc l\'hydre.","Soit le chiffre 46 sur la lettre des constellations.","","Et pour la longitude, cela dit :","\'Au Nord, la même que ci- dessus, mais avec des ailes cette fois\'.","Le dragon ?","Soit le chiffre 15."],"image":null},{"id":10,"text":["La première colonne est celle de l\'oublié :","- La plus au Sud coupée par l\'axe central : Capricorne - 43.","- La plus au sud-Ouest du Sud-Ouest : le Sagittaire - 1.","","La deuxième colonne est celle de Marianne Chauve :","- La plus longue au sud : L\'hydre - 46.","- Au Nord, la même que ci-dessus, mais avec des ailes cette fois : le dragon - 15.","","La troisième colonne est celle d\'Elodie Bouchard :","- Les 2 cornus du Sud Est. Du plus à l\'Ouest au plus à l\'Est : Bélier, Taureau - 28,4.","- Les 2 oiseaux. Du plus à l\'Est au plus à l\'Ouest : Cygne, Aigle - 46,5."],"image":null},{"id":11,"text":["Moi j\'obtiens :","43.46284, 1.15465","On part fouiller là-bas ?"],"image":null},{"id":12,"text":["Vous avez visionné la VHS ?","C\'est Garraud dessus, on le voit à 15 minutes, il n\'a pas l\'air bien.","Et à la fin, cette personne, c\'était probablement quelqu\'un de proche…"],"image":null},{"id":13,"text":["D\'après la lettre, ce proche, c\'est la mère de Garraud.","En regardant la boîte aux lettres et les archives, on voit qu\'elle s\'appelait Jacqueline Houdin."],"image":null}]},{"id":"box1help4","title":"Le chemin de la vérité","status":"closed","hints":[{"id":1,"text":["Il faut donc classer les victimes selon 3 catégories :","- Victime du grand œuvre du tueur au Tarot (tueur organisé)","- Victime collatérale du Tueur au Tarot (tueur organisé)","- Victime de Charles Garraud (tueur désorganisé)","Avez-vous étudié le guide du profiler ? Cela doit pouvoir nous aider"],"image":null},{"id":2,"text":["Parmi tous les meurtres,","quatre se ressemblent,","un semble avoir été exécuté de manière plus impulsive","et pour le dernier, nous n\'avons même pas de cartes de tarot."],"image":null},{"id":3,"text":["Le classement des victimes nous donne donc :","- Rebecca Dumont : Victime du grand œuvre du tueur au Tarot.","- Marianne Chauve : Victime du grand œuvre du tueur au Tarot.","- Elodie Bouchard : Victime du grand œuvre du tueur au Tarot.","- Xavier Monrency : Victime du grand œuvre du tueur au Tarot.","- Nicolas Chaussee : Victime de Charles Garraud.","- Jacqueline Houdin : Victime collatérale du Tueur au Tarot"],"image":null},{"id":4,"text":["On a toutes les victimes maintenant,","il ne nous reste qu\'à trouver le chemin des astres."],"image":null},{"id":5,"text":["Je crois qu\'il nous a laissé un indice à chaque fois avec un symbole et un triangle.","On a donc une carte et 2 mots cryptés.","Ces symboles, il me semble les avoir vus quelque part.","Ce ne serait pas son alphabet codé ?"],"image":null},{"id":6,"text":["J\'ai décodé...","\'Orléans\' et \'la Source\'","...avec l\'alphabet trouvé sur le site du fan.","Avec Google map, si je rentre l\'adresse, je tombe sur un quartier.","Il ne me reste plus qu\'à trouver le lieu précis grâce à la carte qu\'on a récupérée, ça ne doit pas être très loin."],"image":null},{"id":7,"text":["Moi j\'atterris au","\'Parc Départemental des Dolines de Limère\'","sur Google Map.","Ou sur \'Route de Saint Cyr, Ardon\' ou sur \'Rte d\'Ardon, Ardon\'.","Allons fouiller là-bas maintenant."],"image":null}]}]' WHERE id = 1;
UPDATE help SET data = E'[{"id":"box2help1","title":"Premier tri de la liste des victimes","status":"open","hints":[{"id":1,"text":["Tout d\'abord, vérifions que nous avons bien tout pour résoudre cette piste.","Je pense qu\'il va nous falloir :","- La liste des victimes décédées près de leur lieu de naissance.","- Les papiers donnés par Garraud lors de son interrogatoire (correspondances avec sa fan et étranges dessins)"],"image":null},{"id":2,"text":["Intéressez-vous aux correspondances entre Garraud et sa fan.","Vous remarquez quelque chose ?"],"image":null},{"id":3,"text":["Il y a vraiment beaucoup de fautes d\'orthographe…"],"image":null},{"id":4,"text":["Voici tous les mots que j\'ai relevés sur la feuille 1 :","Ah drèsse toix amare thym riz charre leu mât thon deux làs pris son.","","Sur la feuille 2 :","Lait rêvés las cions ah riz voeux, do nœud l\'or riz gas mi A la proche aine vis z\'y t\'e z\'eux.","En les lisant à voix haute on a une phrase :","\'Adresse toi à Martin Richard le maton de la prison. Les révélations arrivent, donne l\'origami à la prochaine visiteuse.\'","On devrait interroger ce gardien de prison."],"image":null},{"id":5,"text":["Avez-vous réussi à faire l\'origami avec les étranges papiers de Garraud, en vous aidant de la page du livre que vous a donné Martin ?","Sinon, il existe plein de tuto disponibles sur internet pour ce genre de modèle d\'Origami.","Il s\'agit de la double étoile ou ou de l\'étoile traditionnelle si ça peut vous aider."],"image":null},{"id":6,"text":["Il y a écrit quelque chose sur l\'origami :","Homo Signorum.","À chaque signe est associé un organe, c\'est indiqué dessus."],"image":null},{"id":7,"text":["On a donc un homme zodiacal.","Le tueur nous indique donc qu\'il prend un organe en fonction du signe astrologique de la personne.","Je vous conseille de vérifier pour chaque organe si la date de naissance de la victime correspond, c\'est plus rapide.","Par exemple, pour la tête, il faut que la victime soit bélier donc née entre le 21 mars et le 19 avril.","Toutes les victimes qui ont été décapitées et sont nées hors de ces dates ne devraient pas correspondre à notre tueur.","On peut déjà supprimer pas mal de victimes, il devrait vous en rester 8 à la fin."],"image":null},{"id":8,"text":["Il me reste :","- Aaron King","- Annina Kurschner","- Daisy Vandenbulcke","- Elimena Furino","- Horasiu Prunea","- Jörn Frenzel","- Konstantin Wallner","- Riano Della Valle."],"image":null}]},{"id":"box2help2","title":"Le secret de Garraud","status":"closed","hints":[{"id":1,"text":["Vous avez vu les news à la télé ?","Sinon, regardez dans votre Historique"],"image":null},{"id":2,"text":["Vous avez vu sur le reportage, il y a une image du forum on dirait."],"image":null},{"id":3,"text":["Ce forum, c\'est \'fan des constellations cachées\'.","Il faudrait qu\'on se connecte aux messages privés de Garraud.","Il a sûrement dû nous communiquer son pseudo et son mot-de-passe avant de se suicider."],"image":"assets/renforts/box2_secret_renfort3_forum.jpg"},{"id":4,"text":["Vous avez bien inspecté la cellule de Garraud ?"],"image":null},{"id":5,"text":["Vous avez demandé une analyse du vomi à Adèle ?"],"image":null},{"id":6,"text":["\'tueur55\',","uniquement des lettres et des chiffres…"],"image":null},{"id":7,"text":["En rentrant :","\'Rabid Star Slayer\' dans le pseudo,","et \'tueur55\' en mot de passe,","on peut accéder aux messages privés…","Allez voir c\'est une mine d\'informations !"],"image":null},{"id":8,"text":["Vous avez fait une recherche sur Stella Louiseberg ?","Je tombe sur une youtubeuse qui parle d\'un livre.","Et on y retrouve la théorie de Lilith équilibrée dont parle Garraud avec le tueur sur le forum.","Il est téléchargeable en lien !","Voici sa chaîne si vous n\'arrivez pas à l\'avoir : ","https://www.youtube.com/@LaurAstrologie"],"image":null},{"id":9,"text":["Sur le forum, le tueur parle de \'la théorie de Lilith équilibrée\'.","Il me semble que c\'est le nom d\'un des paragraphes du livre…"],"image":"assets/renforts/box2_secret_renfort9_lilith_balance.jpg"},{"id":10,"text":["Il semblerait donc que le tueur choisisse ses victimes lorsque Lilith est en balance.","Ça devrait nous permettre d\'éliminer de nouvelles victimes de la liste."],"image":null}]},{"id":"box2help3","title":"Modus Operandi","status":"closed","hints":[{"id":1,"text":["Il y a une partie des périodes où Lilith est en balance dans le livre,","mais il semblerait qu\'il manque des pages et donc des périodes après 1963.","La youtubeuse nous apprend comment faire un thème astral.","On pourrait le faire pour les dernières victimes et vérifier par nous-même ?"],"image":null},{"id":2,"text":["Il nous reste donc :","- Aaron King","- Annina Kurschner","- Jörn Frenzel","- Konstantin Wallner","- Riano Della Valle"],"image":null}]},{"id":"box2help4","title":"Le meurtre de Mason Reynolds","status":"closed","hints":[{"id":1,"text":["Vous avez été visiter la scène de crime chez Mason Reynolds ?","L\'adresse sur le rapport, c\'est","39 Southway Colchester CO 27 BA."],"image":null},{"id":2,"text":["Avez-vous observé la webcam de Mason ?","Elle clignote...","Peut-être que vous pourriez la faire analyser par Tim ?"],"image":null},{"id":3,"text":["On dirait que Mason essaie de cacher quelque chose.","Vous avez fouillé sous le canapé ?"],"image":null},{"id":4,"text":["On a une lettre écrite par une certaine Stella...","Explorons les autres objectifs pour voir si on ne trouverait pas un lien quelque part."],"image":null},{"id":5,"text":["Avez-vous feuilleté le livre dont parle la youtubeuse ?","Regardez qui l\'a écrit…"],"image":null},{"id":6,"text":["Le nom était donc","Stella Louiseberg."],"image":null}]},{"id":"box2help5","title":"Trouver Stella","status":"closed","hints":[{"id":1,"text":["On a un premier bout d\'adresse sur la lettre qu\'on a retrouvée chez Mason, mais la ville est effacée.","Où pourrait-on la trouver ?"],"image":null},{"id":2,"text":["Vous avez feuilleté le livre de Stella Louiseberg ?"],"image":null},{"id":3,"text":["L\'adresse est sur l\'épitaphe,","sur la deuxième page."],"image":null},{"id":4,"text":["L\'adresse est donc","20 Avenue Charles de Gaulle, Fouras."],"image":null}]},{"id":"box2help6","title":"Dernière épreuve","status":"closed","hints":[{"id":1,"text":["Le tueur semble s\'intéresser à Lauren et son collègue :","François Duchamp.","Avez-vous pensé à l\'interroger ?"],"image":null},{"id":2,"text":["Des photos de Lauren...","Un texto de menace...","Il faut se rendre au bureau de la Detective Box pour voir ce qu\'il se passe !"],"image":null}]}]' WHERE id = 2;
UPDATE help SET data = E'[{"id":"box3help1","title":"Remonter la piste de Lauren","status":"open","hints":[{"id":1,"text":["Tout d\'abord, vérifions que nous avons bien tout pour résoudre cette piste.","Je pense qu\'il va nous falloir :","- Le cold case d\'Annina Kürschner.","Il vous manque quelque chose ?","Allez voir dans l\'onglet Historique"],"image":null},{"id":2,"text":["Il semblerait qu\'il y ait un site de boîte mail en ligne, irhemail,","et l\'adresse qu\'elle utilisait était","\'anninakurschner131@irhemail.com\'","Il nous manque son mot de passe.","Vous avez bien fouillé les affaires retrouvées dans le bureau de Lauren ?"],"image":null},{"id":3,"text":["On peut également demander à Tim d\'analyser la boîte mail pour trouver le mot de passe ?"],"image":null},{"id":4,"text":["Il y a un message intéressant sur cette boîte qui dit qu\'elle a reçu un mail de :","Tarot-XX","sur un site de rencontre : ","\'treffenderlehrer\'","Vous avez essayé de vous connecter pour voir ?"],"image":null},{"id":5,"text":["Le site demande ensuite la chanson préférée d\'Annina.","Il y a une certaine Leonie Brugmann qui lui demande si elle vient à un cours de musique.","Il est possible qu\'elle ait une idée à ce sujet, vous avez essayé de l\'interroger ?"],"image":null},{"id":6,"text":["C\'était 99luftballons.","Elle avait du goût.","Allons voir sur la messagerie maintenant."],"image":null},{"id":7,"text":["La conversation avec Tarot-XX est la plus intéressante.","On y apprend que notre tueur est une femme et qu\'elle a donné rendez-vous à Annina lors de ses vacances à Zurich, avant sa mort."],"image":null},{"id":8,"text":["Le point de rendez-vous initial était","\'Aussichtspavillion Wutöschingen\'","mais elles sont allées à un lieu préhistorique.","Vous avez regardé sur Google Maps autour de ce lieu s\'il y en avait ?"],"image":null},{"id":9,"text":["Elles se sont rendues au","Dolmen von Degernau."],"image":null}]},{"id":"box3help2","title":"Localiser l\'échantillon","status":"open","hints":[{"id":1,"text":["Tout d\'abord, vérifions que nous avons bien tout pour résoudre cette piste.","Je pense qu\'il va nous falloir :","- L\'échantillon de terre","- Le rapport d\'analyse botanique d\'Adèle","- L\'herbier","- Le briquet"],"image":null},{"id":2,"text":["Lisez attentivement le rapport d\'analyse rédigé par Adèle.","Avez-vous bien pris note des informations qu\'elle vous donne sur les plantes ?"],"image":null},{"id":3,"text":["L\'une des plantes est de la famille des rosacées et l\'autre est de la famille des géraniacées.","Avez-vous consulté les différentes variétés présentes sur l\'herbier pour comparer ?"],"image":null},{"id":4,"text":["Avez-vous fait un tri des variétés de plantes en fonction des informations données par Adèle ? (couleur, origine, forme, odeur).","Par élimination, il devrait vous rester une plante de chaque famille, qui proviennent du même endroit."],"image":null},{"id":5,"text":["Moi j\'obtiens en rosacée :","le prunier du Portugal (arbuste qui pousse essentiellement au Portugal, et uniquement dans les Pyrénées-Atlantiques en France).","Et en géraniacée :","l\'érodium de Manescau (fleurs violettes endémiques des Pyrénées-Atlantiques, on en trouve dans la forêt des Arbailles en France)."],"image":null},{"id":6,"text":["Ce sont les deux seules variétés qu\'on trouve au même endroit. Nous avons notre localisation :","la forêt des Arbailles","mais il faudrait affiner d\'avantage."],"image":null},{"id":7,"text":["Vous êtes-vous intéressé au briquet ?","Lauren ne fumait pas, ce briquet me paraît étrange."],"image":null},{"id":8,"text":["Le briquet s\'ouvre et il y a les notes de Lauren à l\'intérieur.","Cela parle d\'Annina et du lieu qui lui rappelle sa jeunesse.","Vous devriez résoudre l\'objectif \'Remonter la piste de Lauren\' pour aller plus loin."],"image":"assets/renforts/box3_echantillon_renfort7_papier_briquet.jpg"},{"id":9,"text":["Le tueur a fait visiter un Dolmen à Annina.","Est-ce qu\'il n\'y en aurait pas un dans la forêt des Arbailles ?"],"image":null},{"id":10,"text":["Sur Google Maps, je trouve le Dolmen d\'Ité.","C\'est probablement là-bas qu\'il faut se rendre."],"image":null},{"id":11,"text":["Nous voici dans la forêt des Arbailles.","Avez-vous vu l\'inscription sur la pierre ?"],"image":null},{"id":12,"text":["Vous avez vu le poème d\'Albert Samain récupéré dans la planque de la tueuse ?","On y voit toute une série de constellations :","- le pégase","- le dragon","- Cassiopée","- Andromède","- la lyre","- la Vierge","- le cygne","- et enfin le Chariot."],"image":null},{"id":13,"text":["Vous avez vu sur les arbres ?","Ce sont des constellations qui sont gravées."],"image":null},{"id":14,"text":["Suivons ces symboles et creusons au pied du signe du chariot pour découvrir ce qu\'il y a de caché ici…"],"image":"assets/renforts/box3_foret_renfort4_all_signs.jpg"}]},{"id":"box3help3","title":["Localiser les potentielles cibles restantes"],"status":"closed","hints":[{"id":1,"text":["Fouillez bien dans le coffre trouvé dans la forêt.","Il y a plusieurs choses à déduire.","Vous pouvez déjà commencer par faire analyser le squelette à Adèle."],"image":null},{"id":2,"text":["Observez bien la vieille photo trouvée dans le coffre.","Il y a deux détails importants à relever : un nom et un numéro.","Ce bracelet ne vous dit rien ?"],"image":null},{"id":3,"text":["Sur le dossier accroché au lit, je lis le nom de la patiente :","Estelle Barloigus.","Et à son poignet est accroché le même bracelet médical que vous avez en votre possession.","Il porte le numéro : 865249.","Ces informations ne pourraient-elles pas vous permettre d\'accéder à un dossier médical ?"],"image":null},{"id":4,"text":["Est-ce que vous êtes allé sur le site de l\'ASCLEA ?"],"image":null},{"id":5,"text":["Dans la section \'histoire\', on peut accéder aux dossiers.","Nous avons le nom, le prénom et le numéro..."],"image":null},{"id":6,"text":["Souvenez-vous du programme de greffe.","On peut se connecter dessus avec un nom et un numéro de patient.","Vous allez pouvoir en apprendre plus sur Estelle Barloigus et sa maladie.","On peut donc accéder à son dossier en tapant :","Estelle - Barloigus - 865249."],"image":null},{"id":7,"text":["Avez-vous pensé à interroger le Dr Yann Lacroix sur le cas d\'Estelle Barloigus ?","Il pourrait vous en apprendre plus sur l\'échec de sa greffe."],"image":null},{"id":8,"text":["Valluy, ce nom ne vous dit rien ?","Vous avez pensé à aller voir les archives ?"],"image":null},{"id":9,"text":["Céline ne répond pas, c\'est étrange.","Vous avez essayé d\'aller chez elle ?","On a son adresse sur l\'enveloppe du poème qu\'elle nous a envoyé dans la box 1."],"image":null},{"id":10,"text":["Vous n\'avez pas trouvé ?","Son adresse est 8 rue des Lilas à Bordeaux."],"image":null},{"id":11,"text":["Il y a beaucoup de choses à observer chez Céline, fouillez-bien partout."],"image":null},{"id":12,"text":["Avez-vous trouvé tous ces éléments :","- l\'arbre généalogique","- le reçu","- le dossier médical","- le livre ?"],"image":null},{"id":13,"text":["Il y a une preuve de dépôt indiquant que la dernière box vous a été envoyée par Céline !","Avez-vous regardé ce qu\'il y a d\'écrit au verso ?","Si c\'est elle qui nous a envoyé la boîte, pourquoi vouloir nous le faire savoir ?"],"image":null},{"id":14,"text":["Avez-vous ouvert les bords de la boîte ?","Vous devriez trouver une liste de noms rédigée par Estelle Barloigus.","Observez bien la liste, il y a des personnes qui n\'ont pas encore été tuées…","Il y a les deux que nous ne connaissions pas encore :","Maria Gruber et Giuseppe Rossi."],"image":null}]},{"id":"box3help4","title":"La prochaine victime","status":"closed","hints":[{"id":1,"text":["Pour choisir quelle est la dernière cible, il faut que l\'on trouve celle qui est encore en vie et celle qui a déjà été tuée.","Elle en parle dans sa dernière lettre non ?"],"image":null},{"id":2,"text":["On a eu deux cartes de tarot avec cette lettre.","Elles doivent représenter les deux dernières cibles."],"image":null},{"id":3,"text":["Il faut donc qu\'on sache quelle carte représente quelle personne.","Il y a des descriptions dans la planque de Céline sur la vie de chacune."],"image":null},{"id":4,"text":["Est-ce que le manuel de tarot ne pourrait pas nous aider ?"],"image":null},{"id":5,"text":["D\'après le manuel de tarot, Maria semble associée à la carte du Chariot"],"image":null}]},{"id":"box3help5","title":"Dernière épreuve","status":"closed","hints":[{"id":1,"text":["Voyons voir,","elle dit qu\'il faut qu\'on utilise le dos des cartes de tarot,","mais est-ce qu\'on aurait un indice pour les assembler ?"],"image":null},{"id":2,"text":["Dans la planque de Céline,","on dirait bien que les cartes sont placées dans un certain ordre sur le tableau en liège.","Mais il faut savoir quelle carte correspond à qui."],"image":null},{"id":3,"text":["Le profiling de Lauren pourrait nous aider pour cela, non ?"],"image":null},{"id":4,"text":["Donc si j\'assemble les cartes en les replaçant et que je les retourne, j\'obtiens une constellation.","Qu\'en faire maintenant ?","Vous avez lu le livre de Stella Louiseberg ?","Que disaient les pages manquantes ?"],"image":null},{"id":5,"text":["Les gens de la liste ont été choisis selon plusieurs critères :","Lilith en balance, leur zodiaque et...","leur lieu de naissance qui était sur la constellation de la mère."],"image":null},{"id":6,"text":["Est-ce qu\'on pourrait retracer la constellation grâce à la Detective Board ?","Tous ces morts sur leur lieu de naissance, ce sont nos victimes.","J\'espère que vous avez mis les points de leur lieu de mort à jour pour gagner du temps !"],"image":null},{"id":7,"text":["Je crois qu\'on pourrait même décalquer cette constellation au dos des cartes.","Tiens la pochette transparente reçue en box 2 pourrait nous servir de calque !"],"image":null},{"id":8,"text":["Maintenant il faut qu\'on regarde sur quelles villes tombent les points de la constellation qui nous manquent..."],"image":null},{"id":9,"text":["Les 2 dernières villes sur lesquelles tombent la constellation sont","Milan et Graz"],"image":null},{"id":10,"text":["Maria, la dernière victime sur la liste est autrichienne"],"image":null}]},{"id":"box3help6","title":"Identifier le tueur","status":"closed","hints":[{"id":1,"text":["Loïc Valluy...","Ce nom ne vous dit rien ?"],"image":null}]}]' WHERE id = 3;


